5,121.59827828407288],[24.97856975051998,121.59735560417175],[24.978355793229913,121.59750580787659],[24.978132110210538,121.5973663330078],[24.978015405865023,121.59750580787659],[24.97794732827899,121.59711956977843],[24.97765556676926,121.5967547893524],[24.977636115977337,121.59528493881226],[24.97749023493986,121.59524202346802],[24.9778111729939,121.59465193748474],[24.977898701408776,121.59381508827208],[24.978579475842484,121.59324645996094],[24.978735080898257,121.59274220466615],[24.979600630427736,121.59173369407655],[24.979483927475687,121.59135818481444],[24.979162993786517,121.59088611602782]];                    
